class Converter{constructor(t,e,i){this.form=document.querySelector(t),this.inputs=this.form.querySelectorAll(e),this.resp=this.form.querySelector(i),this.inPxEm=this.form.querySelector("#inPxEm"),this.inEmPx=this.form.querySelector("#inEmPx"),this.inDefault=this.form.querySelector("#inDefault")}addSubmitEvent(){this.form.addEventListener("submit",t=>{t.preventDefault(),this.calculate()})}calculate(){let t=Number(this.inDefault.value);this.verify(t),this.getValue(t)}verify(t){if(!t||!this.inEmPx.value&&!this.inPxEm.value)throw"Please, insert a valid value."}getValue(t){let e=this.inPxEm.value?this.calculateEm(Number(this.inPxEm.value),t):this.calculatePx(Number(this.inEmPx.value),t);this.displayResult(e)}calculateEm(t,e){let i=`${t/e}em`;return i}calculatePx(t,e){let i=`${t*e}px`;return i}displayResult(t){this.resp.value=t}addClickEvent(){this.inputs.forEach(t=>{t.addEventListener("click",()=>{let e=t.id;this.clearInput(e)})})}clearInput(t){"inPxEm"===t?this.inEmPx.value="":this.inPxEm.value=""}}const calculator=new Converter("form",".inValue",".resp");calculator.addClickEvent(),calculator.addSubmitEvent();

